// src/app/programs/[id]/page.jsx

import React from 'react';
import Image from 'next/image';
import Link from 'next/link';
import { notFound } from 'next/navigation';
import listings from '../../../lib/data/programs.json';        // summary JSON
// detail JSON

// Pre-render one page per `id`
export function generateStaticParams() {
  return listings.map((p) => ({ id: p.id }));
}

export const dynamicParams = false;

export default function ProgramDetailPage({ params }) {
  const summary = listings.find((p) => p.id === params.id);
  const detail  = details .find((d) => d.id === params.id);

  if (!summary || !detail) {
    return notFound();
  }

  // merge both objects
  const programme = { ...summary, ...detail };

  return (
    <section className="py-16 px-4 md:px-12 bg-gray-50 min-h-screen">
      <div className="max-w-3xl mx-auto space-y-8">
        {/* Page title & intro */}
        <h1 className="text-5xl font-bold">{programme.pageTitle}</h1>
        {programme.intro && (
          <p className="text-lg text-gray-700">{programme.intro}</p>
        )}

        {/* Subtitle / heading */}
        {programme.subtitle && (
          <h2 className="text-3xl font-semibold text-green-600">
            {programme.subtitle}
          </h2>
        )}
        {programme.heading && (
          <h3 className="text-2xl font-bold">{programme.heading}</h3>
        )}
        {programme.description && (
          <p className="text-gray-700">{programme.description}</p>
        )}

        {/* Price & details */}
        {programme.price && (
          <div className="flex gap-4">
            <span className="font-semibold text-green-600">Price</span>
            <span>{programme.price}</span>
          </div>
        )}
        {programme.details && (
          <div className="mt-4 text-gray-700">{programme.details}</div>
        )}

        {/* Features list */}
        {programme.features && (
          <ul className="list-disc list-inside text-gray-700 mt-3 space-y-1">
            {programme.features.map((f, i) => (
              <li key={i}>{f}</li>
            ))}
          </ul>
        )}

        {/* Extra paragraph */}
        {programme.extra && (
          <p className="mt-4 text-gray-700">{programme.extra}</p>
        )}

        {/* Podcast embed */}
        {programme.podcastText && (
          <>
            <p className="mt-6 text-gray-700">{programme.podcastText}</p>
            <div
              className="mt-2"
              dangerouslySetInnerHTML={{ __html: programme.podcastEmbed }}
            />
          </>
        )}

        {/* Image */}
        {programme.image && (
          <div className="relative w-full h-64 mt-8">
            <Image
              src={programme.image}
              alt={programme.title}
              fill
              className="object-cover rounded-lg"
              priority
            />
          </div>
        )}

        {/* Back link */}
        <div className="mt-12 text-center">
          <Link href="/programs" className="text-green-600 hover:underline">
            ← Back to all programs
          </Link>
        </div>
      </div>
    </section>
  );
}
