import React from 'react';
import Image from 'next/image';
import Link from 'next/link';
import { notFound } from 'next/navigation';
import programsData from '../../../../lib/programs.json';

export function generateStaticParams() {
  const programs = programsData.consultations || [];
  return programs.map((prog) => ({ programId: prog.id }));
}

export default function ProgramDetailPage({ params }) {
  const programs = programsData.consultations || [];
  const program = programs.find((p) => p.id === params.programId);

  if (!program) {
    return notFound();
  }

  return (
    <section className="py-16 px-4 md:px-12 bg-gray-50 min-h-screen">
      <div className="max-w-3xl mx-auto space-y-8">
        <h1 className="text-5xl font-bold">{program.title}</h1>
        <p className="text-lg text-gray-700">{program.description}</p>
        {program.image && (
          <div className="relative w-full h-64 mt-8">
            <Image
              src={program.image}
              alt={program.title}
              fill
              className="object-cover rounded-lg"
              priority
            />
          </div>
        )}
        {program.consultations?.length > 0 && (
          <div>
            <h2 className="text-2xl font-semibold text-green-600">Available Consultations</h2>
            <ul className="list-disc list-inside text-gray-700 mt-3 space-y-1">
              {program.consultations.map((c) => (
                <li key={c.id}>
                  <Link href={`/athletes/programmes/${c.id}`} className="text-green-600 hover:underline">
                    {c.pageTitle || c.title || 'View Consultation'}
                  </Link>
                </li>
              ))}
            </ul>
          </div>
        )}
        <div className="mt-12 text-center">
          <Link href="/athletes/programmes" className="text-green-600 hover:underline">
            ‚Üê Back to all programs
          </Link>
        </div>
      </div>
    </section>
  );
}
