'use client';

import React from 'react';
import { motion } from 'framer-motion';
import Link from 'next/link';
import { Space_Grotesk } from 'next/font/google';
import Image from 'next/image';

// Font setup
const spaceGrotesk = Space_Grotesk({
  subsets: ['latin'],
  variable: '--font-space-grotesk',
  weight: ['300', '400', '500', '600', '700'],
});

// Animation variants
const containerVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.6,
      staggerChildren: 0.2,
    },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
};

// Define the Service interface for type safety
interface Service {
  id: string;
  title: string;
  description: string;
  image: string;
  consultations?: Array<{
    id: string;
    title: string;
    consultations: Array<{
      id: string;
      title?: string;
      pageTitle?: string;
      intro?: string;
      description?: string;
      price?: string;
      details?: string;
      features?: string[];
      idealFor?: string;
    }>;
  }>;
  detailedContent: {
    overview: string;
    price?: string;
    duration?: string;
    protocol?: string[];
    dataObtained?: string[] | Record<string, string[]>;
    idealFor?: string;
    quote?: { text: string; author: string };
    services?: Array<{
      name: string;
      description?: string;
      price?: string;
      duration?: string;
      validity?: string;
      details?: string[];
      protocol?: string[];
      tests?: string[];
      dataObtained?: string[] | Record<string, string[]>;
      idealFor?: string;
      report?: string;
    }>;
    booking?: boolean;
  };
}

interface PerformanceLabDetailClientProps {
  service: Service;
}

const PerformanceLabDetailClient: React.FC<PerformanceLabDetailClientProps> = ({ service }) => {
  const { detailedContent } = service;

  const detailedContentRender = (
    <div className="mt-8 space-y-8">
      <h3 className="text-3xl sm:text-4xl font-bold text-gray-800 dark:text-gray-200">{service.title}</h3>
      <p className="text-lg sm:text-xl text-gray-700 dark:text-gray-300">{detailedContent.overview}</p>

      {detailedContent.quote && (
        <blockquote className="border-l-4 border-green-600 pl-4 italic text-gray-600 dark:text-gray-400">
          {detailedContent.quote.text}
          <span className="block mt-2 font-semibold text-gray-800 dark:text-gray-200">
            {detailedContent.quote.author}
          </span>
        </blockquote>
      )}

      {detailedContent.services ? (
        <>
          <h4 className="text-2xl sm:text-3xl font-semibold text-gray-800 dark:text-gray-200">
            Our {service.title} Services
          </h4>
          {detailedContent.services.map((subService, index) => (
            <div key={index} className="space-y-4">
              <h5 className="text-xl sm:text-2xl font-semibold text-gray-800 dark:text-gray-200">
                {subService.name}
              </h5>
              {subService.description && (
                <p className="text-lg text-gray-700 dark:text-gray-300">{subService.description}</p>
              )}
              <ul className="list-disc pl-6 text-lg text-gray-700 dark:text-gray-300">
                {subService.price && (
                  <li>
                    <strong>Price:</strong> {subService.price}
                  </li>
                )}
                {subService.duration && (
                  <li>
                    <strong>Duration:</strong> {subService.duration}
                  </li>
                )}
                {subService.validity && (
                  <li>
                    <strong>Valid:</strong> {subService.validity}
                  </li>
                )}
              </ul>
              {(subService.details || subService.protocol) && (
                <p className="text-lg text-gray-700 dark:text-gray-300">
                  <strong>{subService.protocol ? 'Protocol:' : 'Details:'}</strong>
                  <ul className="list-disc pl-6">
                    {(subService.details || subService.protocol || []).map((item, idx) => (
                      <li key={idx}>{item}</li>
                    ))}
                  </ul>
                </p>
              )}
              {subService.tests && (
                <p className="text-lg text-gray-700 dark:text-gray-300">
                  <strong>Tests:</strong>
                  <ul className="list-disc pl-6">
                    {subService.tests.map((test, idx) => (
                      <li key={tx}>{test}</li>
                    ))}
                  </ul>
                </p>
              )}
              {subService.dataObtained && (
                <p className="text-lg text-gray-700 dark:text-gray-300">
                  <strong>Data Obtained:</strong>
                  {typeof subService.dataObtained === 'object' && !Array.isArray(subService.dataObtained) ? (
                    Object.keys(subService.dataObtained).map((category, idx) => (
                      <div key={idx} className="mt-2">
                        <strong>{category}:</strong>
                        <ul className="list-disc pl-6">
                          {subService.dataObtained[category].map((item, i) => (
                            <li key={i}>{item}</li>
                          ))}
                        </ul>
                      </div>
                    ))
                  ) : (
                    <ul className="list-disc pl-6">
                      {(subService.dataObtained as string[]).map((item, idx) => (
                        <li key={idx}>{item}</li>
                      ))}
                    </ul>
                  )}
                </p>
              )}
              {subService.idealFor && (
                <p className="text-lg text-gray-700 dark:text-gray-300">
                  <strong>Ideal for:</strong> {subService.idealFor}
                </p>
              )}
              {subService.report && (
                <p className="text-lg text-gray-700 dark:text-gray-300">{subService.report}</p>
              )}
            </div>
          ))}
        </>
      ) : (
        <>
          <ul className="list-disc pl-6 text-lg text-gray-700 dark:text-gray-300">
            {detailedContent.price && (
              <li>
                <strong>Price:</strong> {detailedContent.price}
              </li>
            )}
            {detailedContent.duration && (
              <li>
                <strong>Duration:</strong> {detailedContent.duration}
              </li>
            )}
          </ul>
          {detailedContent.protocol && (
            <p className="text-lg text-gray-700 dark:text-gray-300">
              <strong>Protocol:</strong>
              <ul className="list-disc pl-6">
                {detailedContent.protocol.map((item, idx) => (
                  <li key={idx}>{item}</li>
                ))}
              </ul>
            </p>
          )}
          {detailedContent.dataObtained && (
            <p className="text-lg text-gray-700 dark:text-gray-300">
              <strong>Data Obtained:</strong>
              <ul className="list-disc pl-6">
                {(detailedContent.dataObtained as string[]).map((item, idx) => (
                  <li key={idx}>{item}</li>
                ))}
              </ul>
            </p>
          )}
          {detailedContent.idealFor && (
            <p className="text-lg text-gray-700 dark:text-gray-300">
              <strong>Ideal for:</strong> {detailedContent.idealFor}
            </p>
          )}
        </>
      )}

      {(detailedContent.booking || service.title === 'Lactate Threshold') && (
        <div className="mt-8">
          <h4 className="text-2xl sm:text-3xl font-semibold text-gray-800 dark:text-gray-200">
            Ready to Book?
          </h4>
          <Link href="/booking">
            <span className="inline-block mt-4 text-green-600 dark:text-green-400 hover:underline text-lg font-semibold">
              Book Online
            </span>
          </Link>
        </div>
      )}
    </div>
  );

  const consultationsRender = service.consultations?.length > 0 && (
    <div className="mt-12 space-y-8">
      <h3 className="text-3xl sm:text-4xl font-bold text-gray-800 dark:text-gray-200">
        Consultation Packages
      </h3>
      {service.consultations.map((consultationGroup, index) => (
        <div key={index} className="space-y-6">
          {consultationGroup.consultations.map((consultation, idx) => (
            <div key={idx} className="space-y-4">
              <h4 className="text-2xl sm:text-3xl font-semibold text-gray-800 dark:text-gray-200">
                {consultation.title || consultation.pageTitle}
              </h4>
              {consultation.intro && (
                <p className="text-lg text-gray-700 dark:text-gray-300 italic">{consultation.intro}</p>
              )}
              {consultation.description && (
                <p className="text-lg text-gray-700 dark:text-gray-300">{consultation.description}</p>
              )}
              {consultation.price && (
                <p className="text-lg text-gray-700 dark:text-gray-300">
                  <strong>Price:</strong> {consultation.price}
                </p>
              )}
              {consultation.details && (
                <p className="text-lg text-gray-700 dark:text-gray-300">
                  <strong>Details:</strong> {consultation.details}
                </p>
              )}
              {consultation.features && (
                <p className="text-lg text-gray-700 dark:text-gray-300">
                  <strong>Features:</strong>
                  <ul className="list-disc pl-6">
                    {consultation.features.map((feature, i) => (
                      <li key={i}>{feature}</li>
                    ))}
                  </ul>
                </p>
              )}
              {consultation.idealFor && (
                <p className="text-lg text-gray-700 dark:text-gray-300">
                  <strong>Ideal for:</strong> {consultation.idealFor}
                </p>
              )}
            </div>
          ))}
        </div>
      ))}
    </div>
  );

  return (
    <div className={`min-h-screen ${spaceGrotesk.variable}`}>
      <section className="relative py-16 sm:py-20 md:py-24 bg-gradient-to-br from-gray-50 to-white dark:from-gray-900 dark:to-black">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          className="container mx-auto px-4"
        >
          <motion.div variants={itemVariants} className="flex flex-col items-center">
            <div className="relative w-full max-w-3xl h-64 sm:h-96">
              <Image
                src={service.image}
                alt={`${service.title} - Performance Lab Service`}
                fill
                sizes="(max-width: 768px) 100vw, 50vw"
                className="object-cover rounded-lg"
                priority={true}
              />
            </div>
            <div className="mt-8 text-center">
              <h1 className="text-4xl sm:text-5xl font-black uppercase bg-clip-text text-transparent bg-gradient-to-r from-green-600 to-green-400 dark:from-green-400 dark:to-green-200">
                {service.title}
              </h1>
              <p className="mt-4 text-lg sm:text-xl text-gray-700 dark:text-gray-300">{service.description}</p>
            </div>
            <div className="mt-8 w-full max-w-4xl">
              {detailedContentRender}
              {consultationsRender}
            </div>
          </motion.div>
        </motion.div>
      </section>
    </div>
  );
};

export default PerformanceLabDetailClient;
