'use client';
import React from 'react';
import SportIcon from './SportIcon';

const AboutUsSection = () => {
  return (
    <section className="flex mt-32 ml-3.5 w-full text-black max-w-[1494px] max-md:mt-10 max-md:max-w-full">
      {/* Main Article with notches */}
      <article className="relative flex flex-col grow shrink-0 py-16 pr-20 pl-20 bg-lime-400 border border-white border-solid rounded-[32px] overflow-visible max-md:pl-8 max-md:pr-8">
        {/* Left notch */}
        <div
          className="absolute -left-8 top-1/2 w-[60px] h-[60px] bg-black rounded-full 
                     transform -translate-y-1/2"
        />
        {/* Right notch */}
        <div
          className="absolute -right-8 top-1/2 w-[60px] h-[60px] bg-black rounded-full 
                     transform -translate-y-1/2"
        />

        <h2 className="self-center text-6xl font-bold leading-none max-md:text-4xl">
          About us
        </h2>

        <div className="flex flex-wrap gap-5 justify-between mt-12 w-full text-4xl text-center leading-[56px] max-w-[1153px] max-md:mt-10 max-md:max-w-full">
          <SportIcon />
          <p className="max-md:max-w-full">
            Learn more about our mission to redefine sports medicine and athlete care.
          </p>
        </div>
      </article>
    </section>
  );
};

export default AboutUsSection;
