'use client';
import React from 'react';
import { motion } from 'framer-motion';

// Animation variants
const containerVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.6,
      staggerChildren: 0.2,
    },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
};

export default function FounderSection() {
  return (
    <section className="relative py-12 px-4 sm:px-6 lg:px-8 bg-gradient-to-b from-black to-gray-900">
      <motion.div
        variants={containerVariants}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
        className="relative z-10 max-w-3xl mx-auto px-6 text-center text-white space-y-4"
      >
        {/* Notched Heading */}
        <motion.article
          variants={itemVariants}
          className="relative flex flex-col py-10 w-full bg-lime-400 border border-white border-solid rounded-[32px] overflow-visible"
        >
          {/* Left notch */}
          <div
            className="absolute -left-8 top-1/2 w-[60px] h-[60px] bg-black rounded-full transform -translate-y-1/2 max-md:-left-4 max-md:w-[40px] max-md:h-[40px]"
          />
          {/* Right notch */}
          <div
            className="absolute -right-8 top-1/2 w-[60px] h-[60px] bg-black rounded-full transform -translate-y-1/2 max-md:-right-4 max-md:w-[40px] max-md:h-[40px]"
          />

          <h2 className="self-center text-3xl font-bold text-black">
            Founded in 2024, 5 Sports Hospital has become a beacon of excellence in sports medicine and orthopedic care.
          </h2>
        </motion.article>

        <motion.p
          variants={itemVariants}
          className="text-lg text-gray-200"
        >
          Our visionary founders, Dr. Pooja Jhade and Dr. Nataraj H M, established a center that combines cutting-edge medical technology with personalized care—dedicated to helping athletes achieve <span className="text-lime-400">peak performance</span>.
        </motion.p>
      </motion.div>
    </section>
  );
}
