'use client';

import { motion } from 'framer-motion';
import { CheckCircle } from 'lucide-react';
import { useTheme } from './../../components/contex/ThemeContext';
import { Space_Grotesk } from 'next/font/google';
import WavyBackground from './../../components/global/Background/WavyBackground';

const spaceGrotesk = Space_Grotesk({
  subsets: ['latin'],
  variable: '--font-space-grotesk',
  weight: ['300', '400', '500', '600', '700'],
});

const features = [
  {
    icon: <CheckCircle className="w-10 h-10" />,
    title: 'Trusted by Elite Athletes & Teams',
    description: 'Preferred by top athletes and professional sports teams for our specialized care.',
  },
  {
    icon: <CheckCircle className="w-10 h-10" />,
    title: '11 Specialized Medical Fields',
    description: 'A multidisciplinary team offering cutting-edge treatments across multiple domains.',
  },
  {
    icon: <CheckCircle className="w-10 h-10" />,
    title: 'Advanced Diagnostic & Treatment Technology',
    description: 'Equipped with the latest innovations for precise assessment and faster recovery.',
  },
  {
    icon: <CheckCircle className="w-10 h-10" />,
    title: 'World-Class Rehab & Performance Center',
    description: 'A state-of-the-art facility designed for rehabilitation, fitness, and peak performance.',
  },
  {
    icon: <CheckCircle className="w-10 h-10" />,
    title: 'Proven Recovery Success',
    description: 'Over 100+ athletes successfully rehabilitated and returned to peak condition.',
  },
];

const containerVariants = {
  hidden: { opacity: 0 },
  visible: { opacity: 1, transition: { staggerChildren: 0.2 } },
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
};

export default function WhyChooseUs() {
  const { darkMode } = useTheme();

  return (
    <section className={`font-space-grotesk ${spaceGrotesk.variable}`}>
      <WavyBackground height="auto" className="py-12 md:py-16 px-4 md:px-20">
        <div className="container mx-auto max-w-5xl">
          {/* Notched Header */}
          <section className="flex w-full text-black max-md:flex-col mb-10">
            <article className="relative flex flex-col py-8 md:py-10 w-full bg-lime-400 border border-white border-solid rounded-[32px] overflow-visible">
              {/* Left notch */}
              <div className="absolute -left-8 top-1/2 w-[60px] h-[60px] bg-black rounded-full hidden md:block transform -translate-y-1/2" />
              {/* Right notch */}
              <div className="absolute -right-8 top-1/2 w-[60px] h-[60px] bg-black rounded-full hidden md:block transform -translate-y-1/2" />

              <h2 className="self-center text-4xl md:text-6xl font-bold leading-none uppercase">
                Why Choose Us?
              </h2>
              <p className="mt-4 text-lg sm:text-xl md:text-2xl text-center max-w-xl sm:max-w-2xl md:max-w-3xl mx-auto px-4 text-black">
                Discover what makes us the <span className="text-green-600 font-bold">#1 Sports Medicine & Rehab Center</span> in India.
              </p>
            </article>
          </section>

          {/* Horizontal Scroll (Marquee Style) */}
          <motion.div
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
            className="relative overflow-hidden w-full py-4"
          >
            <motion.div
              className="flex space-x-6 md:space-x-8 w-full"
              animate={{ x: ['0%', '-100%'] }}
              transition={{
                repeat: Infinity,
                duration: 40,
                ease: 'linear',
              }}
            >
              {[...features, ...features].map((feature, index) => (
                <motion.div
                  key={index}
                  variants={itemVariants}
                  className={`flex-shrink-0 w-[280px] md:w-[300px] p-6 rounded-xl shadow-lg transition-all duration-300 hover:shadow-xl hover:-translate-y-1 border ${
                    darkMode ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-200'
                  }`}
                >
                  <div className="flex flex-col items-center text-center">
                    {/* Icon */}
                    <div
                      className={`relative mb-5 flex items-center justify-center w-16 h-16 rounded-full shadow-md ${
                        darkMode ? 'bg-green-600 text-white' : 'bg-green-100 text-green-700'
                      }`}
                    >
                      <div className="absolute -top-4 left-1/2 transform -translate-x-1/2 bg-green-500 rounded-full p-2">
                        <span className="text-white text-xl">üèÄ</span>
                      </div>
                      {feature.icon}
                    </div>

                    {/* Title */}
                    <h3
                      className={`text-lg font-bold uppercase tracking-tight mb-3 ${
                        darkMode ? 'text-lime-400' : 'text-green-600'
                      }`}
                    >
                      {feature.title}
                    </h3>

                    {/* Description */}
                    <p
                      className={`text-sm leading-relaxed ${
                        darkMode ? 'text-gray-300' : 'text-gray-600'
                      }`}
                    >
                      {feature.description}
                    </p>
                  </div>
                </motion.div>
              ))}
            </motion.div>
          </motion.div>
        </div>
      </WavyBackground>
    </section>
  );
}
