"use client";

import React, { useState } from "react";
import SportIcon from "./SportIcon";

const CardComponent: React.FC = () => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <section className="flex justify-center items-center w-screen h-screen bg-neutral-900">
      <article
        className="relative bg-lime-400 border border-white h-[283px] rounded-[32px] w-[462px]"
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        style={{
          transform: isHovered ? "scale(1.02)" : "scale(1)",
          transition: "transform 0.3s ease",
        }}
      >
        <div className="absolute left-[47px] top-[34px]">
          <h2 className="text-4xl font-bold text-black leading-[55.6px] max-w-[248px]">
            <span>It's Not Just</span>
            <br />
            <span>About Skills</span>
          </h2>
          <p className="mt-6 text-xl leading-7 text-black max-w-[380px]">
            Modern sports are demanding. Without building strength, speed, and
            resilience, you risk falling behind.
          </p>
        </div>
        <div className="absolute top-px right-px bg-black rounded-none h-[75px] w-[92px]">
          <SportIcon />
        </div>
      </article>
    </section>
  );
};

export default CardComponent;
